import React, { Component, useEffect, useState } from "react";

import { getUserList } from "../Data/data-users.js"
import { Avatar } from "../Avatar/Avatar";
import { UserInfo } from "../UserInfo/UserInfo";
import { GridButton } from "../GridButton/GridButton.jsx";
import { ListButton } from "../ListButton/ListButton.jsx";
import { Grid } from "../Grid/Grid.jsx";

import "./UserList.css"

const UserList = () =>{
    const [selected, setSelected] = useState(true);
    const [users, setUsers] = useState([]);
    const [inputValue, setInputValue] = useState ("");
    const [filteredUsers, setFilteredUsers] = useState([]);


    useEffect(() => {
        getUserList()
        .then(users => {
            setUsers(users);
            setFilteredUsers(users);
        })
    },[])

    const onListClick = () =>{
        setSelected(true)
    };

    const onGridClick = () =>{
        setSelected(false)
    };

    const refreashPage = () =>{
        setInputValue("")
        getUserList()
            .then(users => {
                setUsers(users);
                setFilteredUsers(users)
            })

    };

    const onTyping = (event) => {
        setInputValue(event.target.value);
        const filtered = users.filter(user => user.name.first.includes(event.target.value))
        setFilteredUsers(filtered)
    }

    return (
        <div className="main-container">
            <button type="Submit" id="grid-button" onClick={refreashPage}><i className="fas fa-redo"></i></button>
            <div className="btn-container">
                <ListButton onClick={onListClick} />
                <GridButton onClick={onGridClick}/>
            </div>
            <div className="search-div">
                <input type="text" value={inputValue} onChange={onTyping}/>
            </div>
            <div className="main-div">
            {
            filteredUsers.map((user, index) => selected ? 
                (
                    <div className="single-user" key={index}>
                        <Avatar userLogo={user}/>
                        <UserInfo userData={user}/>
                    </div>
                )
                :
                (
                    <div className="single-grid-user" key={index}>
                        <Grid userGridData={user}/>
                    </div>  
                )
            )
            }
            </div>
        </div>
    )
};


